package java_package;

public class TwentyToForty {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int b=20;b<=40;b++)
		{
			if(b%2 == 0)
			{
				System.out.println("even "+b);
			}
			else
			{
				System.out.println("odd "+b);
			}
		}
	}

}
